// ServerForm.js
import React, { useState } from 'react';
// Import the styled function from MUI
import { styled } from '@mui/system';

// Create a custom styled button
const StyledButton = styled(Button)`
  background-color: #007bff; /* Set the background color */
  color: #fff; /* Set the text color */
  &:hover {
    background-color: #0056b3; /* Change the background color on hover */
  }
`;

function ServerForm({ onSubmit }) {
  const [formData, setFormData] = useState({ id: '', name: '', language: '', framework: '' });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onSubmit(formData);
    setFormData({ id: '', name: '', language: '', framework: '' });
  };

  return (
    <form onSubmit={handleSubmit}>
      <input type="text" name="id" value={formData.id} onChange={handleInputChange} placeholder="ID" />
      <input type="text" name="name" value={formData.name} onChange={handleInputChange} placeholder="Name" />
      <input type="text" name="language" value={formData.language} onChange={handleInputChange} placeholder="Language" />
      <input type="text" name="framework" value={formData.framework} onChange={handleInputChange} placeholder="Framework" />
      {/* Use the custom styled button */}
      <StyledButton type="submit">Create Server</StyledButton>
    </form>
  );
}

export default ServerForm;
